mapper.select:
select m.manager_id,m.manager_name,d.dept_name from manager m left join dept d on m.dept_no = d.dept_no
where m.manager_name like concat('%',#{managerName},'%')
order by m.manager_id limit #{pageStart},#{pageSize}

mapper.insert:
insert into manager (manager_name,dept_no) values (#{managerName},(select dept_no from dept where dept_name = #{deptName}));
insert into userlogin (user_id,password,user_name,role) values
((select last_insert_id()),#{password},#{managerName},#{role})

mapper.select:
${sql}


